#include <stdio.h>
#include <string.h>
#include <stdlib.h>
#include <stdbool.h>

char* onedeletechar(char* s, int i){
    char* temp = (char*) malloc((strlen(s)+1)*sizeof(char));
    char* temp2 = (char*) malloc((strlen(s)+1)*sizeof(char));
    strncpy(temp, s, i);
    temp[i] = '\0';
    strncpy(temp2, s+i+1, strlen(s)-i-1);
    temp2[strlen(s)-i-1] = '\0';
    strcat(temp, temp2);
    temp[strlen(s)-1] = '\0';
    return temp;
}

bool isPalindrome(char* s){
    for(int i = 0; i < strlen(s) / 2 ; i++){
        if(s[i] != s[strlen(s) - 1 - i]){
            return false;
        }
    }
    return true;
}

bool validPalindrome(char* s){
    int len = strlen(s);
    int number = 0;
    if(len == 0 || len == 1){
        return true;
    }
    if(isPalindrome(s)){
        return true;
    }
    for(int i = 0; i < strlen(s) / 2; i++){
        char* temp = (char*) malloc((strlen(s)+1)*sizeof(char));
        char* temp2 = (char*) malloc((strlen(s)+1)*sizeof(char));
        temp = onedeletechar(s, i);
        temp2 = onedeletechar(s, strlen(s) - i - 1);
        if(isPalindrome(temp) || isPalindrome(temp2)){
            return true;
        }
        free(temp);
        free(temp2);
    }
    return false;
}

int main(){
    char* s = "";
    bool result = validPalindrome(s);
    printf("%d", result);
    return 0;
}